  function handleNewCat() {
    const date = new Date();
    const abc = "abcdefghijklmnopqrstuvwxyz!#$%";
    const ranNum = Math.floor(Math.random() * 100);
    const ranNum2 = Math.floor(Math.random() * 100);
    const ranLet = abc[Math.floor(Math.random() * abc.length)];
