          spent -= nextTransaction[ii][5];
        }
      }
      nextBoxVal[x] = [nextBoxVal[x][0], nextBoxVal[x][1], nextBoxVal[x][2], spent, nextBoxVal[x][4]];

    }
    setSavings(tempSavings)
    setBoxvalue(nextBoxVal);
    setTransaction(nextTransaction);
